const changeCities = (str: string) => {
  let temp = [];
  if (actCitiesTab.value === str) {
    actCitiesTab.value = "0";
    temp = cities.value;
  } else {
    actCitiesTab.value = str;
    temp = cities.value.filter((item: any) => item.type === str);
  }
  if (temp.length) {
    const uniqueArray = ref<{ type: string; name: string }[]>([]);
    const seenNames = new Set();
    // 对数组 temp 对比 name 属性去重
    for (const item of temp) {
      if (!seenNames.has(item.name)) {
        uniqueArray.value.push(item);
        seenNames.add(item.name);
      }
    }
    actCities.value = uniqueArray.value;
  } else {
    actCities.value = cities.value;
  }
};

