const dataAllUnauthorized = async (str: string) => {
  let _form = ruleForm;
  let strs = ``;
  for (let key in _form) {
    if (_form.hasOwnProperty(key)) {
      let value = (_form as { [key: string]: any })[key];
      // 如果 value 为空 或者null 或者undefined 或者 空数组 就跳过
      if (
        !value ||
        value === null ||
        value === undefined ||
        value === "" ||
        value.length <= 0
      ) {
        continue;
      }
      // 处理数组类型的值，将它们转换成逗号分隔的字符串
      if (Array.isArray(value)) {
        value = value.join(",");
      }
      strs += `**${key}**:${value};`;
    }
  }
  // 去除最后一个&
  strs = strs
    .replace(/name/g, "姓名")
    .replace(/tel/g, "联系方式")
    // .replace(/email/g, "邮箱")
    .replace(/address/g, "地址")
    // .replace(/FromMe/g, "来源")
    .replace(/checkServe/g, "服务");
  // .replace(/sms/g, "备注");
  strs = strs.substring(0, strs.length - 1);

  // 如果str 为空 则赋值 填写正常
  if (str === "") {
    str = "填写正常";
  }
  // 将 strs 种 name 替换为 姓名, tel 替换为 联系方式 email 替换为 邮箱 address 替换为 地址 FromMe 来源 checkServe 服务 sms 备注
